package com.thirdparty.passway._base;

/**
 * 
 * @author Luke
 *
 */
public interface IPasswayApi {
	
    public enum EnumPasswayMode {
        // 直接扣款协议
        DirectPay, 
        // 订单扣款协议
        OrderPay, 
        // 网页扣款协议
        WebPay
    }
    
    public enum EnumPasswayOrderStatus {
        // 未处理
        U, 
        // 成功
        S, 
        // 待查证
        R, 
        // 处理异常（保留状态，用于网络异常或未知异常时）
        E, 
        // 失败
        F,
    }
}
