package com.thirdparty.passway._data;

import java.util.Map;

/**
 * @author Luke
 */
public class AbstractRequestData {

	// 预留字段，传入通道特定参数设置信息，如商户号等信息
	private Map<String, ?> extraParams;

	// 预留字段，传入通道特定参数设置信息，如密钥等
	private String extra1;

	// 通道方分配渠道编号
	private String passwayChannelNo;

	// 通道方分配商户编号
	private String passwayMercNo;

	// 本地商户编号ID
	private String mercCode;

	// 终端IP
	private String clientIp;

	// 回调地址信息
	private String callbackUrl;

	/**
	 * @return the extra1
	 */
	public String getExtra1() {
		return extra1;
	}

	/**
	 * @param extra1
	 *            the extra1 to set
	 */
	public void setExtra1(String extra1) {
		this.extra1 = extra1;
	}

	public Map<String, ?> getExtraParams() {
		return extraParams;
	}

	public void setExtraParams(Map<String, ?> extraParams) {
		this.extraParams = extraParams;
	}

	public String getPasswayChannelNo() {
		return passwayChannelNo;
	}

	public void setPasswayChannelNo(String passwayChannelNo) {
		this.passwayChannelNo = passwayChannelNo;
	}

	public String getPasswayMercNo() {
		return passwayMercNo;
	}

	public void setPasswayMercNo(String passwayMercNo) {
		this.passwayMercNo = passwayMercNo;
	}

	public String getMercCode() {
		return mercCode;
	}

	public void setMercCode(String mercCode) {
		this.mercCode = mercCode;
	}

	public String getClientIp() {
		return clientIp;
	}

	public void setClientIp(String clientIp) {
		this.clientIp = clientIp;
	}

	public String getCallbackUrl() {
		return callbackUrl;
	}

	public void setCallbackUrl(String callbackUrl) {
		this.callbackUrl = callbackUrl;
	}
}
